package net.epictimes.owl.di;import net.epictimes.owl.OwlApplication;import net.epictimes.owl.data.ApiModule;import javax.inject.Singleton;import dagger.BindsInstance;import dagger.Component;import dagger.android.AndroidInjectionModule;@Singleton@Component(modules = {        ApiModule.class,        AndroidInjectionModule.class,        ActivityBuilderModule.class})public interface SingletonComponent {    @Component.Builder    interface Builder {        @BindsInstance        Builder application(OwlApplication application);        Builder apiModule(ApiModule apiModule);        SingletonComponent build();    }    void inject(OwlApplication owlApplication);}